import React, {Component} from "react";
// import Footer from "./components/Footer";
// import Header from "./components/Header";
//import api from "./services/api";

import "./App.css";
//import { list } from "postcss";
//import { timeout } from "q";

class App extends Component {
    constructor() {
        super()
        this.state={
            loading: false,
            quiz: [],
            listAnwser: [],
            category: "",
            question: "",
            correct_answer: "",
            incorrect_answers1: "",
            incorrect_answers2: "",
            incorrect_answers3: "",
            difficulty: "",
            type: "" 

        }

    }
    componentDidMount() {
        let lista = []
        this.setState({loading : true})
        fetch("https://opentdb.com/api.php?amount=5")
            .then(response => response.json())
            //.then(data => console.log(data))
            .then(data => {
                //console.log(data)
                this.state.quiz.push(data)
                data.results[0].incorrect_answers.map(incorrect=>{
                    lista.push(incorrect)
                })

                lista.push(data.results[0].correct_answer)

                //console.log(lista) //

                this.setState({
                    loading: false,
                    listAnwser : lista,
                    quiz : this.state.quiz

                })
                console.log(this.state.quiz[0].results[0])
                console.log(this.state.listAnwser)
                this.setState({question:this.state.quiz[0].results[0].question})
                this.setState({category:this.state.quiz[0].results[0].category})
                this.setState({correct_answer:this.state.quiz[0].results[0].correct_answer})
                this.setState({difficulty:this.state.quiz[0].results[0].difficulty})
                this.setState({type:this.state.quiz[0].results[0].type})

                // if (this.state.type === "multiple") {
                //     this.setState({incorrect_answers1:this.state.quiz[0].results[0].incorrect_answers[0]})
                //     this.setState({incorrect_answers2:this.state.quiz[0].results[0].incorrect_answers[1]})
                //     this.setState({incorrect_answers3:this.state.quiz[0].results[0].incorrect_answers[2]})
                // }
                // else {
                //     this.setState({incorrect_answers1:this.state.quiz[0].results[0].incorrect_answers})
                // }
            })

    }
    render() {
        const frist = "primeiro"
        const second = "segundo"

        const date = new Date()
        const hours = date.getHours()
        let timeOfDay

        if (hours<12){
            timeOfDay = "Bom dia"
        } else if (hours>=12 && hours <17){
            timeOfDay = "Boa tarde"
        } else {
            timeOfDay = "Boa noite"
        }

        const text = this.state.loading ? "loading..." : this.state.quiz.question
        return( 
            <div>
                <nav>
                <h1>teste {frist + " " + second }</h1>
                <h2>olar {`${frist}`}</h2>
                <h3>{timeOfDay}!! Vamos jogar trivia?</h3>
                {/* <Header/>
                <Footer/> */}

                {/* mudar a questao e o numero! */}
                <h5>Questao XX:</h5>
                </nav>
                <main>
                <p>{text}</p>
                <p>{this.state.question}</p>

                {/* PRINTAR AS ALTERNATIVAS NA LISTA DE QUESTOES. */}
                <p> {this.state.listAnwser[0]} </p>




                {/* <h4>{JSON.stringify(this.state.quiz[0])}</h4> */}
                </main>
            </div> 
        )
    }
}

export default App
