import React, {Component} from "react";
// import Footer from "./components/Footer";
//import Header from "./components/Header";
//import api from "./services/api";
import Alternatives from  "./components/Alternatives";
import Question from  "./components/Question";

import "./App.css";
//import { list } from "postcss";
//import { timeout } from "q";

class App extends Component {
    constructor() {
        super()
        this.state={
            loading: false,
            quiz: [],
            listAnwser: [],
            category: "",
            question: "",
            correct_answer: "",
            id: 0,
            difficulty: "",
            type: "" 
        }
    }
    async componentDidMount(){
        let a = await this.getAPI()
        console.log(a)
    }
    async getAPI() {
        let lista = []
        this.setState({loading : true})
        console.log("entrou no componentDidMount ")
        await fetch("https://opentdb.com/api.php?amount=5")

        //fetch("https://opentdb.com/api.php?amount=10&type=boolean")

            .then(response => response.json())
            //.then(data => console.log(data))
            .then(data => {
                console.log("fetch")
                this.state.quiz.push(data)
                data.results[0].incorrect_answers.map(
                    incorrect=>{lista.push(incorrect)
                })
                lista.push(data.results[0].correct_answer)   
                this.makeSuffle(lista)                

                this.setState({
                    loading: false,
                    listAnwser : lista,
                    quiz : this.state.quiz
                })

                console.log("fora")
                //console.log(this.state.quiz[0].results[0])
                //results[i] para cada resultad

                //<Question question={this.state.question}/>
                // this.setState({question:this.state.quiz[0].results[0].question})
                // this.setState({category:this.state.quiz[0].results[0].category})
                // this.setState({correct_answer:this.state.quiz[0].results[0].correct_answer})
                // this.setState({difficulty:this.state.quiz[0].results[0].difficulty})
                // this.setState({type:this.state.quiz[0].results[0].type})   
                return true
            })

    }

    makeSuffle(lista){
        for (var i = lista.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = lista[i];
            lista[i] = lista[j];
            lista[j] = temp;
        }
    }


    render() {
        // const frist = "primeiro"
        // const second = "segundo"
        console.log("AAAAAA")

        const date = new Date()
        const hours = date.getHours()
        let timeOfDay

        if (hours<12){
            timeOfDay = "morning"
        } else if (hours>=12 && hours <17){
            timeOfDay = "afternon"
        } else {
            timeOfDay = "night"
        }

        const text = this.state.loading ? "loading..." : this.state.quiz.question

        return( 
            <div>
                <nav>
                {/* <h1>teste {frist + " " + second }</h1>
                <h2>oala {`${frist}`}</h2> */}

                <h3>Good {timeOfDay}!! Let's play trivia?</h3>
                {/* <Header/>
                <Footer/>nbhgfgh */}

                </nav>
                <main>
                <p>{text}</p>
                <Question quiz={this.state.quiz} id={this.state.id}/>

                {/* fazer um for  com um random pra printar as alternativas */}

                <Alternatives answer={this.state.listAnwser[0]} />
                <Alternatives answer={this.state.listAnwser[1]} />
                <Alternatives answer={this.state.listAnwser[2]} />
                <Alternatives answer={this.state.listAnwser[3]} />



                {/* <h4>{JSON.stringify(this.state.quiz[0])}</h4> */}
                </main>
            </div> 
        )
    }
}

export default App
