import React, {Component} from "react";
import Footer from "./components/Footer";
import Header from "./components/Header";
//import api from "./services/api";

import "./App.css";
//import { timeout } from "q";

class App extends Component {
    constructor() {
        super()
        this.state={
            loading: false,
            quiz: [],
            category: "",
            question: "",
            correct_answer: "",
            incorrect_answers: "",
            difficulty: "",
            type: ""
        }

    }
    componentDidMount() {
        this.setState({loading : true})
        fetch("https://opentdb.com/api.php?amount=5")
            .then(response => response.json())
            //.then(data => console.log(data))
            .then(data => {
                //console.log(data)
                this.state.quiz.push(data)
                this.setState({
                    loading: false,
                    quiz : this.state.quiz
                })
                console.log(this.state.quiz[0].results[0])
                this.setState({question:this.state.quiz[0].results[0].question})
                this.setState({question:this.state.quiz[0].results[0].category})
                this.setState({question:this.state.quiz[0].results[0].correct_answer})
                this.setState({question:this.state.quiz[0].results[0].incorrect_answers})
                this.setState({question:this.state.quiz[0].results[0].difficulty})
            })

    }
    render() {
        const frist = "primeiro"
        const second = "segundo"

        const date = new Date()
        const hours = date.getHours()
        let timeOfDay

        if (hours<12){
            timeOfDay = "Bom dia"
        } else if (hours>=12 && hours <17){
            timeOfDay = "Boa tarde"
        } else {
            timeOfDay = "Boa noite"
        }

        const text = this.state.loading ? "loading..." : this.state.quiz.question
        return( 
            <div>
                <nav>
                <h1>teste {frist + " " + second }</h1>
                <h2>olar {`${frist}`}</h2>
                <h3>{timeOfDay}!! Vamos jogar trivia?</h3>
                {/* <Header/>
                <Footer/> */}
                {/* mudar a questao e o numero */}
                <h5>Questao XXXX</h5>
                </nav>
                <main>
                <p>{text}</p>
                {/* descobrir como printar so a pergunta */}
                <p>{this.state.question}</p>
                {/* <p>{this.state.category}</p>
                <p>{this.state.correct_answer}</p>
                <p>{this.state.incorrect_answers}</p>
                <p>{this.state.type}</p> */}
                {/* <h4>{JSON.stringify(this.state.quiz[0])}</h4> */}
                </main>
            </div> 
        )
    }
}

export default App
