import React, {Component} from "react";
import Footer from "./components/Footer";
import Header from "./components/Header";
//import api from "./services/api";

import "./App.css";
//import { timeout } from "q";

class App extends Component {
    constructor(){
        super()
        this.state={
            quiz: {}

        }
    }
    componentDidMount() {
        fetch("https://opentdb.com/api.php?amount=1")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    quiz : data
                })
            })
    }
    render() {
        const frist = "primeiro"
        const second = "segundo"

        const date = new Date()
        const hours = date.getHours()
        let timeOfDay

        if (hours<12){
            timeOfDay = "Bom dia"
        } else if (hours>=12 && hours <17){
            timeOfDay = "Boa tarde"
        } else {
            timeOfDay = "Boa noite"
        }

        return(
            <div>
                <nav>
                <h1>teste {frist + " " + second }</h1>
                <h2>olar {`${frist}`}</h2>
                <h3>{timeOfDay}!! Vamos jogar trivia?</h3>
                <p>paragrafo dsdasdas</p>
                <Header/>
                <Footer/>
                </nav>
                <main>
                <p>resto do site...</p>
                <h4>{this.state.quiz.question}</h4>
                </main>
            </div>
        )
    }
}

export default App
