import React, {Component} from "react";
import Footer from "./components/Footer";
import Header from "./components/Header";
//import api from "./services/api";

import "./App.css";
//import { timeout } from "q";

class App extends Component {
    constructor() {
        super()
        this.state={
            loading: false,
            quiz: []
        }
    }
    componentDidMount() {
        this.setState({loading : true})
        fetch("https://opentdb.com/api.php?amount=1")
            .then(response => response.json())
            //.then(data => console.log(data))
            .then(data => {
                console.log(data)
                this.state.quiz.push(data)
                this.setState({
                    loading: false,
                    quiz : this.state.quiz
                })
            })
    }
    render() {
        const frist = "primeiro"
        const second = "segundo"

        const date = new Date()
        const hours = date.getHours()
        let timeOfDay

        if (hours<12){
            timeOfDay = "Bom dia"
        } else if (hours>=12 && hours <17){
            timeOfDay = "Boa tarde"
        } else {
            timeOfDay = "Boa noite"
        }

        const text = this.state.loading ? "loading..." : this.state.quiz.question
        return( 
            <div>
                <nav>
                <h1>teste {frist + " " + second }</h1>
                <h2>olar {`${frist}`}</h2>
                <h3>{timeOfDay}!! Vamos jogar trivia?</h3>
                <p>paragrafo dsdasdas</p>
                <Header/>
                <Footer/>
                </nav>
                <main>
                <p>resto do site...</p>
                <p>{text}</p>
                <h4>{this.state.quiz}</h4>
                <h4>aaaaa</h4>
                </main>
            </div> 
        )
    }
}

export default App
